// Write a method to return all subsets of a set.//因为没有要求说要按照递增或者递减序列来排列结果，所以说我们就不需要用到Arrays.sort或者Collections.sortpublic class Solution {  public ArrayList<ArrayList<Integer>> subsets(ArrayList<Integer> S) {    ArrayList<ArrayList<Integer>> res = new ArrayList<ArrayList<Integer>>();    if(S == null || S.size() == 0) {      return res;    }    ArrayList<Integer> temp = new ArrayList<Integer>();    res.add(new ArrayList<Integer>(temp));    helper(S, res, temp, 0);    return res;  }  private void helper(ArrayList<Integer> S, ArrayList<ArrayList<Integer>> res,    ArrayList<Integer> temp, int index) {      if(index == S.size()) {        return;      }      for(int i = index; i < S.size(); i++) {        temp.add(S.get(i));        helper(S, res, temp, i + 1);        res.add(new ArrayList<Integer>(temp));        temp.remove(temp.size() - 1);      }    }}